import React, { useEffect, useState } from "react";
import { Form } from "react-bootstrap";
import Navbarr from "./Navbar";
// import Pagination from "./Pagination";
import { useDispatch, useSelector } from "react-redux";
import { deleteUser, loadUser } from "../redux/Action";
import useHistory from "react-router-dom";
// import { GET_USERS } from '../redux/Actiontypes';
import Button from "@material-ui/core/Button";
// import 'bootstrap/dist/css/bootstrap.min.css';
import "../App.css";
// import { Multiselect } from "multiselect-react-dropdown";
// import { connect } from 'react-redux';
import { Collapse } from "react-bootstrap";

const Home = () => {
  const history = useHistory();
  const dispatch = useDispatch();
  const { users } = useSelector((state) => state.data);
  const [searchlistname, setSearchlistname] = useState("");
  const [searchlistadd, setSearchlistadd] = useState("");
  const [searchlistdate, setSearchlistdate] = useState("");

  // const [currentPage, setCurrentPage] = useState(1);
  // const [itemsPerPage] = useState(5);

  // const Lastitems = currentPage * itemsPerPage; //1*5=5
  // const Firstitems = Lastitems - itemsPerPage; //5-5=0
  // const currentitems = users.slice(Firstitems, Lastitems); //(0,5)

  // const paginate = (pagenumber) => setCurrentPage(pagenumber);

  useEffect(() => {
    dispatch(loadUser());
  }, [dispatch]);

  const handledelete = (id) => {
    if (window.confirm("Are you sure wanted to delete the user"))
      dispatch(deleteUser(id));
    dispatch(loadUser());
  };

  // const [option, setUseroption] = useState([]);
  // const onSelect = (e) => {
  //     setUseroption(e);
  // };

  const updatename = (e) => {
    setSearchlistname(e.target.value);
    console.log(setSearchlistname);
  };

  const updateadd = (e) => {
    setSearchlistadd(e.target.value);
  };

  const updatedate = (e) => {
    setSearchlistdate(e.target.value);
  };
  // const [userdata, setUserdata] = useState([])
  // const [name, setName] = useState('')

  // const handleSearch = (e) => {
  //     // const newdata = userdata.filter(x => x.name == name);
  //     // console.log(newdata);
  //     // setUserdata(newdata)
  //     setSearchlist(e.target.value);
  // }
  const [open, setOpen] = useState(false);

  return (
    <div>
      <Navbarr />

      <div className="container">
        <div className="row" style={{ marginBottom: "10px" }}>
          <Button
            onClick={() => setOpen(!open)}
            aria-controls="example-collapse-text"
            aria-expanded={open}
            style={{ width: "200px" }}
          >
            <h3
              style={{
                fontFamily: "Georgia",
                color: "black",
                fontSize: "25px",
              }}
            >
              Filter
            </h3>
          </Button>
          <Collapse in={open}>
            <div id="example-collapse-text">
              <div className="col-md-4">
                <Form.Group className="" controlId="formBasicSearch">
                  <Form.Label> Name</Form.Label>
                  <Form.Control
                    type="search"
                    placeholder="Search By Name"
                    onChange={updatename}
                  />
                  {/* <button onClick={currentitems}>
                                         search
                                        </button> */}
                </Form.Group>
              </div>
              <div className="col-md-4">
                <Form.Label>Address</Form.Label>
                <Form.Control
                  type="search"
                  placeholder="Search By Address"
                  onChange={updateadd}
                  // onChange={(e) => setName(e.target.value)}
                />
              </div>
              <div className="col-md-4">
                <div className="mx-auto">
                  <Form.Group controlId="dob">
                    <Form.Label>Search By Date</Form.Label>
                    <Form.Control
                      type="date"
                      onChange={updatedate}
                      name="dob"
                      // value={dob}
                      placeholder="Date of Birth"
                    />
                  </Form.Group>
                </div>
                {/* <TextField
                            label="Search By Date"
                            type="date"
                            // selected={this.state}
                            onChange={updatedate}
                            InputLabelProps={{
                                shrink: true,
                            }}
                        /> */}
              </div>
            </div>
          </Collapse>

          {/* <div className="col-md-4">
                        <Form.Label>category</Form.Label>
                        <Multiselect
                            name="name"
                            options={currentitems}
                            displayValue="name"
                            onSelect={onSelect}
                            // onChange={update}
                            placeholder="Select Name"
                            emptyRecordMsg="No Name Available"
                            hidePlaceholder
                            value={option.name}
                        />
                    </div> */}

          <div className="col-md-4 offset-md-8">
            <Button
              style={{
                fontFamily: "serif",
                marginLeft: "200px",
                marginTop: "38px",
              }}
              onClick={() => history.push("/adduser")}
              variant="contained"
              color="primary"
            >
              Add User
            </Button>
          </div>
        </div>
      </div>
      <h3
        style={{
          fontFamily: "Georgia, serif",
          color: "#84a5ad",
          fontSize: "35px",
        }}
      >
        Student Data
      </h3>

      <table className="table table-striped table-bordered">
        <thead className="headers">
          <tr>
            <th scope="col">Name</th>
            <th scope="col">Birth Date</th>
            <th scope="col">Gender</th>
            <th scope="col">Contact</th>
            <th scope="col">Address</th>
            {/* <th scope="col">Age</th> */}
            <th scope="col">Action</th>
          </tr>
        </thead>
        <tbody className="table-body">
          {users
            .filter((val) => {
              if (searchlistname === "") {
                return val;
              } else if (
                val.name.toLowerCase().includes(searchlistname.toLowerCase())
              ) {
                return val;
              }
              return false;
            })
            .filter((value) => {
              if (
                value.address
                  .toLowerCase()
                  .includes(searchlistadd.toLowerCase())
              ) {
                return value;
              }
              return false;
            })
            .filter((value) => {
              if (
                value.dob.toLowerCase().includes(searchlistdate.toLowerCase())
              ) {
                return value;
              }
              return false;
            })
            .map((user) => (
              // {currentitems && currentitems.map((user) => (
              <tr key={user.id}>
                <td>{user.name}</td>
                <td>{user.dob}</td>
                <td>{user.gender}</td>
                <td>{user.contact}</td>
                {/* <td>{user.age}</td> */}
                <td>{user.address}</td>
                <td>
                  <button
                    type="button"
                    onClick={() => history.push(`/edituser/${user.id}`)}
                    style={{ padding: "6px 28px", marginRight: "10px" }}
                    className="btn btn-primary"
                  >
                    Edit
                  </button>
                  <button
                    type="button"
                    style={{ padding: "6px 24px" }}
                    onClick={() => handledelete(user.id)}
                    className="btn btn-danger"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
        </tbody>
      </table>
      {/* <Pagination
        postsPerPage={itemsPerPage}
        totalPosts={users.length}
        paginate={paginate}
      /> */}
    </div>
  );
};
export default Home;
