import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import { Button } from "@material-ui/core";
import { useNavigate } from "react-router-dom";
import { useDispatch } from "react-redux";
import { addUser } from "../redux/Action";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import Navbarr from "./Navbar";
import "react-datetime/css/react-datetime.css";
import { Form } from "react-bootstrap";

const Adduser = ({ onFileSelect }) => {
  const classess = useStyless();
  const classes = useStyles();
  let navigate = useNavigate();
  let dispatch = useDispatch();
  const [error, setError] = useState("");
  // const [selectedFile, setSelectedFile] = useState(null);

  const [setGender] = React.useState("");
  const handleChange = (event) => {
    setGender(event.target.value);
  };
  const [state, setState] = useState({
    // name: "", gender: "", contact: "", age: "", address: "",
  });

  const handlesubmit = (e) => {
    e.preventDefault();
    if (!name || !dob || !gender || !contact || !address) {
      setError("Please fill input field");
    } else {
      dispatch(addUser(state));
      navigate("/home");
      setError("");
    }
  };
  const { name, gender, contact, address, dob } = state;

  const handleInputchange = (e) => {
    let { name, value } = e.target;
    setState({ ...state, [name]: value });
  };

  // const fileInput = useRef(null)

  // const handleFileInput = (e) => {
  //     // handle validations
  //     onFileSelect(e.target.files[0])
  // }

  return (
    <>
      <Navbarr />
      <div className="App">
        <h3 style={styling}>ADD USER</h3>
        <form
          onSubmit={handlesubmit}
          className={classes.root}
          noValidate
          autoComplete="off"
        >
          <TextField
            onChange={handleInputchange}
            name="name"
            id="standard-basic"
            label="Name"
            value={name}
            type="text"
          />
          <div className="mx-auto">
            <Form.Group controlId="dob">
              <Form.Label>Birth Date</Form.Label>
              <Form.Control
                onChange={handleInputchange}
                type="date"
                name="dob"
                value={dob}
                placeholder="Date of Birth"
              />
            </Form.Group>
          </div>
          <FormControl className={classess.formControl}>
            <InputLabel id="demo-simple-select-label">Gender</InputLabel>
            <Select
              labelId="demo-simple-select-label"
              id="demo-simple-select"
              value={gender}
              name="gender"
              onSelect={handleChange}
              onChange={handleInputchange}
            >
              <MenuItem value={"male"}>Male</MenuItem>
              <MenuItem value={"female"}>Female</MenuItem>
            </Select>
          </FormControl>
          <br />
          <TextField
            onChange={handleInputchange}
            name="contact"
            id="standard-basic"
            label="Contact"
            value={contact}
            type="number"
          />
          <br />
          {/* <TextField
                        onChange={handleInputchange}
                        name="age"
                        id="standard-basic"
                        label="Age"
                        value={age}
                        type="number" /><br /> */}
          <TextField
            onChange={handleInputchange}
            name="address"
            id="standard-basic"
            label="Address"
            value={address}
            type="text"
          />
          <br />

          {error && (
            <div
              style={{
                color: "red",
                fontSize: "20px",
                fontVariant: "all-small-caps",
                marginLeft: "30%",
              }}
            >
              {error}
            </div>
          )}

          <Button
            onClick={() => navigate("/home")}
            variant="contained"
            style={{ width: "100px" }}
            color="secondary"
          >
            {" "}
            Go Back
          </Button>
          <Button
            style={{ width: "100px" }}
            variant="contained"
            color="primary"
            type="submit"
          >
            {" "}
            Submit
          </Button>
        </form>
      </div>
    </>
  );
};
export default Adduser;

const useStyles = makeStyles((theme) => ({
  root: {
    marginTop: 70,
    "& > *": {
      margin: theme.spacing(1),
      width: "45ch",
    },
  },
}));
const styling = {
  fontFamily: "Georgia, serif",
  marginTop: "100px",
  marginLeft: "20%",
  marginRight: "20%",
  borderRadius: "10px",
  color: "green",
  border: "1px solid green",
};

const useStyless = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
}));
